declare module "apiTypings" {

    export type reqData = {
    /**
     * Type of data to require Eg. Pokemon, Berries, Locations...
     */
    dataToReq: "pokemon" | "berries",
    name?:string,
    id?:number|number[],
    interval?: {
            limit:number,
            offset?:number
        }
    }

    export type callbackFn = (data:any) => Promise<Pokemon> | Promise<NamedAPIResourceList>
    export type returnsVal = Promise<Pokemon | NamedAPIResourceList | null | 0>
}

import { Pokemon, NamedAPIResourceList } from "pokenode-ts"